mapper.select:
SELECT B.BOARD_SEQ, B.BOARD_TYPE, B.TITLE, (SELECT IF( B.DEL_YN ='Y', 'true', 'false') ) as DEL_YN , B.REG_DATE FROM T_BOARD B
AND B.TITLE LIKE CONCAT('%', #{keyword}, '%')
AND B.BOARD_TYPE IN (
#{value}
)
ORDER BY B.REG_DATE DESC

SELECT B.BOARD_SEQ, B.BOARD_TYPE, B.TITLE, (SELECT IF( B.DEL_YN ='Y', 'true', 'false') ) as DEL_YN , B.REG_DATE FROM T_BOARD B
AND B.TITLE LIKE CONCAT('%', #{parameter.keyword}, '%')
AND B.BOARD_TYPE IN (
#{value}
)
ORDER BY B.REG_DATE DESC LIMIT #{pageRequest.limit} , #{pageRequest.offset}

mapper.update:
UPDATE T_BOARD
	SET
		BOARD_TYPE=#{boardType},
		TITLE=#{title},
		CONTENTS =#{contents}
WHERE BOARD_SEQ =#{boardSeq}
